  const [bannerOpen, setBannerOpen] = useState(true);

  return (
    <>
      {bannerOpen && (
        <div {...props} className="banner">
          <div className="banner-inner text-xxs">

          </div>
        </div>
      )}
    </>
  );
}

export default Banner;
